// Get raw output string
let raw = $json.output;

// Remove ```json and ``` if present
raw = raw.replace(/```json/g, "").replace(/```/g, "").trim();

// Parse into object
const parsed = JSON.parse(raw);

// Return in correct n8n format
return {
  json: parsed
};

const data = $json;

// Recursive function to format any nested JSON into text
function formatData(obj, indent = 0) {
  let output = "";
  const pad = " ".repeat(indent);

  if (Array.isArray(obj)) {
    obj.forEach((item, i) => {
      output += `${pad}- ${typeof item === "object" ? "\n" + formatData(item, indent + 2) : item}\n`;
    });
  } else if (typeof obj === "object" && obj !== null) {
    for (const [key, value] of Object.entries(obj)) {
      if (typeof value === "object") {
        output += `${pad}${key}:\n${formatData(value, indent + 2)}`;
      } else {
        output += `${pad}${key}: ${value}\n`;
      }
    }
  } else {
    output += `${pad}${obj}\n`;
  }

  return output;
}

// Generate email body
let emailBody = `Hi,

Here’s your customized trip details:

${formatData(data)}

Have an amazing trip! 🌸
`;

return [
  {
    json: {
      subject: "Your Travel Plan 🌍",
      body: emailBody
    }
  }
];
